<?phpsession_start();include "connec_mysqli.php";if (isset($_POST['refuel'])) {	if (is_numeric($_POST['refuel'])) {		if($_POST['refuel'] >= 0 and $_POST['refuel'] <= 100) { 			$sql = "UPDATE joueurs SET AddFuel = ".$_POST['refuel']." WHERE ID = ".$_SESSION['Pseudo_ID'];			mysqli_query($bdd,$sql) or die(mysql_error());		}	}}if (isset($_POST['retires'])) {	if ($_POST['retires'] == "yes") {		$sql = "UPDATE joueurs SET ChangeTires = 'yes' WHERE ID = ".$_SESSION['Pseudo_ID'];		mysqli_query($bdd,$sql) or die(mysql_error());	}} else if (isset($_POST['retires']) == false) {	$sql = "UPDATE joueurs SET ChangeTires = 'no' WHERE ID = ".$_SESSION['Pseudo_ID'];	mysqli_query($bdd,$sql) or die(mysql_error());}header('Location: ../game_page.php');?>